import React,{useEffect,useState} from 'react'
import NewsItems from './NewsItems'
import PropTypes from 'prop-types';
import InfiniteScroll from "react-infinite-scroll-component";


const News= (props) => {
  const [articles,setArticles] = useState([])
  const [page, setpage] = useState(1)
  const [totalResult, settotalResult] = useState(0)
  //const [loading, setloading] = useState(false)


 const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }


  const updateNews = async()=> {
    props.setProgress(10);
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${page}&pageSize=${props.pageSize}`;
    let data = await fetch(url);
    props.setProgress(40);
    let parseData = await data.json();
    props.setProgress(70);
    setArticles(parseData.articles);
    settotalResult(parseData.totalResults)

    props.setProgress(100);
  }
 useEffect(() => {
   updateNews();
   document.title = `News-Ninja-${capitalizeFirstLetter(props.category)}`;
 },[])


 const fetchMoreData = async () => {


    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${page+1}&pageSize=${props.pageSize}`;
    setpage(page + 1)
    let data = await fetch(url);
    let parseData = await data.json();
    setArticles(articles.concat(parseData.articles));
    settotalResult(parseData.totalResults)
  };





    return (
      <>
        <h2 className='text-center' style= {{marginTop:"80px"}}>NewsMonkey - Top Hedadlines - {capitalizeFirstLetter(props.category)}</h2>
        <InfiniteScroll
          dataLength={articles.length}
          next={fetchMoreData}
          hasMore={articles.length !== totalResult}
          loader={<h4>Loading...</h4>}
        >
          <div className="container">
            <div className="row my-3">
              {articles.map((element) => {
                return <div className='col-md-4 my-3' key={element.url}>
                  <NewsItems key={element.url} title={element.title ? element.title : ""} description={element.description ? element.description : ""} imgurl={element.urlToImage}
                    newsurl={element.url} author={element.author} date={element.publishedAt} source={element.source.name} />
                </div>

              })}
            </div>
          </div>
        </InfiniteScroll>







      </>
    )

}

News.defaultProps = {
  country: 'in',
  pageSize: 9,
  category: 'general'
}
News.propTypes = {
  country: PropTypes.string,
  pageSize: PropTypes.number,
  category: PropTypes.string,

}

export default News
