import React, { useEffect, useState } from "react";
import WeatherItems from "./WeatherItems";
import PropTypes from "prop-types";

const Weather = (props) => {
  const [location, setLocation] = useState({});
  const [current, setCurrent] = useState({});

  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  const updateWeather = async () => {
    props.setProgress(10);
    const url = `https://api.weatherapi.com/v1/current.json?key=${props.apiKey}&q=${props.location}`;
    let data = await fetch(url);
    props.setProgress(40);
    let parseData = await data.json();
    props.setProgress(70);
    setLocation(parseData.location);
    setCurrent(parseData.current);
    props.setProgress(100);
  };
  useEffect(() => {
    updateWeather();
    document.title = `NewEEAAO - ${capitalizeFirstLetter(props.category)}`;
  }, []);

  return (
    <>
      <h1>Ayush</h1>
      <h2 className="text-center" style={{ marginTop: "80px" }}>
        NewEEAAO - {capitalizeFirstLetter(props.category)}
      </h2>
      <div className="container">
        <div className="row my-3">
              <div className="col-md-4 my-3" >
                <WeatherItems
                  location={element.location.name ? element.location.name : ""}
                  region={
                    element.location.region ? element.location.region : ""
                  }
                  country={
                    element.location.country ? element.location.country : ""
                  }
                  localTime={element.location.localtime}
                  tempc={element.current.temp_c ? element.current.temp_c : ""}
                  weatherIcon={
                    element.current.condition.icon
                      ? element.current.condition.icon
                      : ""
                  }
                  wind={
                    element.current.wind_kph ? element.current.wind_kph : ""
                  }
                  windDir={
                    element.current.wind_dir ? element.current.wind_dir : ""
                  }
                />
              </div>
        </div>
      </div>
    </>
  );
};

Weather.defaultProps = {
  location: "Bhilai",
};
Weather.propTypes = {
  location: PropTypes.string,
};

export default Weather;
