import React, { useEffect, useState } from "react";
import MetalItems from "./MetalItems";
import PropTypes from "prop-types";

const Metal = (props) => {
  const [articles, setArticles] = useState([]);

  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  const updateMetal = async () => {
    props.setProgress(10);
    const url = `https://metals-api.com/api/latest?access_key=${props.apiKey}&base=${currency}&symbols=${metalType}`;
    let data = await fetch(url);
    props.setProgress(40);
    let parseData = await data.json();
    props.setProgress(70);
    setArticles(parseData.articles);
    props.setProgress(100);
  };
  useEffect(() => {
    updateMetal();
    document.title = `NewEEAAO - ${capitalizeFirstLetter(props.category)}`;
  }, []);

  return (
    <>
      <h2 className="text-center" style={{ marginTop: "80px" }}>
        NewEEAAO - Top Hedadlines - {capitalizeFirstLetter(props.category)}
      </h2>
      <div className="container">
        <div className="row my-3">
          {articles.map((element) => {
            return (
              <div className="col-md-4 my-3" key={element.url}>
                <MetalItems
                  key={element.url}
                  date={element.date}
                  base={element.base}
                  rates={element.rates.XAU}
                />
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};

Metal.defaultProps = {
  currency: "INR",
  metalType: "XAU",
  category: "metal",
};
Metal.propTypes = {
  currency: PropTypes.string,
  metalType: PropTypes.string,
  category: PropTypes.string,
};

export default Metal;
